$("#bt-login").click(function(){var b=$("#uname").val();var c=$("#upwd").val();var d=/^[a-z0-9]{3,8}$/i;var a=/^[a-z0-9]{3,8}$/i;if(!d.test(b)){alert("用户名格式不正确");return}if(!a.test(c)){alert("密码格式不正确");return}$.ajax({type:"POST",url:"/login",data:{uname:b,upwd:c},success:function(e){if(e.code===1){alert("登录成功！3S后跳转用户中心");sessionStorage.loginName=b;sessionStorage.loginUid=e.uid;location.href="usercenter.html"}else{alert("登录失败")}}})});