$("#bt-register").click(function(){var a=$("#uname").val();var d=$("#upwd").val();var c=$("#upwd1").val();var b=$("#homepage").val();console.log(a);if(a===""){alert("用户名不能为空!");return}if(d===""){alert("密码不能为空!");return}if(c!=d){alert("两次密码不一致!");return}if(b===""){alert("主页地址不能为空!");return}$.ajax({type:"POST",url:"/register",data:{uname:a,upwd:d,homepage:b},success:function(e){if(e.code===1){alert("注册成功!3秒种后自动跳转");setTimeout(function(){location.href="login.html"},3000)}else{alert("注册失败"+e.msg)}}})});